import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import session from "express-session";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Session configuration
app.use(session({
  secret: process.env.SESSION_SECRET || 'attendance-system-secret-key',
  resave: false,
  saveUninitialized: false,
  cookie: {
    secure: false, // Set to true in production with HTTPS
    httpOnly: true,
    maxAge: 24 * 60 * 60 * 1000 // 24 hours
  }
}));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertStudentSchema, insertAttendanceSchema } from "@shared/schema";
import { z } from "zod";

// Extend Express Request interface to include session
declare module 'express-session' {
  interface SessionData {
    userId: string;
    userRole: 'admin' | 'teacher';
  }
}

const loginSchema = z.object({
  username: z.string(),
  password: z.string(),
});

const attendanceSearchSchema = z.object({
  qrCode: z.string(),
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { username, password } = loginSchema.parse(req.body);
      const user = await storage.validateUser(username, password);

      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Store user session (simple implementation)
      req.session.userId = user.id;
      req.session.userRole = user.role;

      res.json({ user: { id: user.id, username: user.username, role: user.role } });
    } catch (error) {
      res.status(400).json({ message: "Invalid request" });
    }
  });

  app.post("/api/auth/logout", async (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ message: "Could not log out" });
      }
      res.json({ message: "Logged out successfully" });
    });
  });

  app.get("/api/auth/me", async (req, res) => {
    if (!req.session.userId) {
      return res.status(401).json({ message: "Not authenticated" });
    }

    const user = await storage.getUser(req.session.userId);
    if (!user) {
      return res.status(401).json({ message: "User not found" });
    }

    res.json({ user: { id: user.id, username: user.username, role: user.role } });
  });

  // Middleware for authentication
  const requireAuth = (req: any, res: any, next: any) => {
    if (!req.session.userId) {
      return res.status(401).json({ message: "Authentication required" });
    }
    next();
  };

  const requireAdmin = (req: any, res: any, next: any) => {
    if (!req.session.userId || req.session.userRole !== 'admin') {
      return res.status(403).json({ message: "Admin access required" });
    }
    next();
  };

  // Students endpoints
  app.get("/api/students", requireAuth, async (req, res) => {
    try {
      const { search, class: className, isActive } = req.query;
      const filters: any = {};

      if (search) filters.search = search as string;
      if (className) filters.class = className as string;
      if (isActive !== undefined) filters.isActive = isActive === 'true';

      const students = await storage.getStudents(filters);
      res.json(students);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch students" });
    }
  });

  app.get("/api/students/:id", requireAuth, async (req, res) => {
    try {
      const student = await storage.getStudent(req.params.id);
      if (!student) {
        return res.status(404).json({ message: "Student not found" });
      }
      res.json(student);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch student" });
    }
  });

  app.post("/api/students", requireAdmin, async (req, res) => {
    try {
      const studentData = insertStudentSchema.parse(req.body);

      // Check if NIS already exists
      const existingStudent = await storage.getStudentByNis(studentData.nis);
      if (existingStudent) {
        return res.status(400).json({ message: "NIS already exists" });
      }

      const student = await storage.createStudent(studentData);
      res.status(201).json(student);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid student data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create student" });
    }
  });

  app.put("/api/students/:id", requireAdmin, async (req, res) => {
    try {
      const updateData = insertStudentSchema.partial().parse(req.body);
      const student = await storage.updateStudent(req.params.id, updateData);
      res.json(student);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid student data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to update student" });
    }
  });

  app.delete("/api/students/:id", requireAdmin, async (req, res) => {
    try {
      await storage.deleteStudent(req.params.id);
      res.json({ message: "Student deleted successfully" });
    } catch (error) {
      res.status(500).json({ message: "Failed to delete student" });
    }
  });

  // QR Code lookup
  app.post("/api/students/search-by-qr", requireAuth, async (req, res) => {
    try {
      const { qrCode } = attendanceSearchSchema.parse(req.body);
      const student = await storage.getStudentByQrCode(qrCode);

      if (!student) {
        return res.status(404).json({ message: "Student not found" });
      }

      res.json(student);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid QR code" });
      }
      res.status(500).json({ message: "Failed to search student" });
    }
  });

  // Attendance endpoints
  app.get("/api/attendance", requireAuth, async (req, res) => {
    try {
      const { studentId, date, startDate, endDate, status, class: className } = req.query;
      const filters: any = {};

      if (studentId) filters.studentId = studentId as string;
      if (date) filters.date = new Date(date as string);
      if (startDate) filters.startDate = new Date(startDate as string);
      if (endDate) filters.endDate = new Date(endDate as string);
      if (status) filters.status = status as string;
      if (className) filters.class = className as string;

      const records = await storage.getAttendanceRecords(filters);
      res.json(records);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch attendance records" });
    }
  });

  app.post("/api/attendance", requireAuth, async (req, res) => {
    try {
      const attendanceData = insertAttendanceSchema.parse({
        ...req.body,
        recordedBy: req.session.userId,
        date: new Date(),
        time: new Date(),
      });

      const record = await storage.createAttendanceRecord(attendanceData);
      res.status(201).json(record);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid attendance data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create attendance record" });
    }
  });

  app.put("/api/attendance/:id", requireAuth, async (req, res) => {
    try {
      const updateData = insertAttendanceSchema.partial().parse(req.body);
      const record = await storage.updateAttendanceRecord(req.params.id, updateData);
      res.json(record);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid attendance data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to update attendance record" });
    }
  });

  // Statistics endpoints
  app.get("/api/stats/dashboard", requireAuth, async (req, res) => {
    try {
      const date = req.query.date ? new Date(req.query.date as string) : new Date();
      const stats = await storage.getAttendanceStats(date);
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  // Reports endpoints
  app.get("/api/reports/attendance", requireAuth, async (req, res) => {
    try {
      const { startDate, endDate, class: className } = req.query;
      const filters: any = {};

      if (startDate) filters.startDate = new Date(startDate as string);
      if (endDate) filters.endDate = new Date(endDate as string);
      if (className) filters.class = className as string;

      const report = await storage.getStudentAttendanceReport(filters);
      res.json(report);
    } catch (error) {
      res.status(500).json({ message: "Failed to generate attendance report" });
    }
  });

  // CSV Export endpoint
  app.get("/api/export/attendance-csv", requireAuth, async (req, res) => {
    try {
      const { startDate, endDate, class: className } = req.query;
      const filters: any = {};

      if (startDate) filters.startDate = new Date(startDate as string);
      if (endDate) filters.endDate = new Date(endDate as string);
      if (className) filters.class = className as string;

      const records = await storage.getAttendanceRecords(filters);

      // Generate CSV content
      const csvHeader = "Tanggal,Waktu,Nama Siswa,NIS,Kelas,Status,Keterangan\n";
      const csvRows = records.map(record => {
        const date = record.date.toLocaleDateString('id-ID');
        const time = record.time.toLocaleTimeString('id-ID');
        const name = `"${record.student.name}"`;
        const nis = record.student.nis;
        const className = `"${record.student.class}"`;
        const status = record.status;
        const notes = `"${record.notes || ''}"`;

        return `${date},${time},${name},${nis},${className},${status},${notes}`;
      }).join('\n');

      const csvContent = csvHeader + csvRows;

      res.setHeader('Content-Type', 'text/csv');
      res.setHeader('Content-Disposition', 'attachment; filename=attendance-report.csv');
      res.send(csvContent);
    } catch (error) {
      res.status(500).json({ message: "Failed to export CSV" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  students,
  attendanceRecords,
  users,
  type User,
  type InsertUser,
  type Student,
  type InsertStudent,
  type AttendanceRecord,
  type InsertAttendance,
  type StudentWithAttendance,
  type AttendanceWithStudent,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, gte, lte, desc, sql, ilike, or } from "drizzle-orm";
import bcrypt from "bcrypt";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  validateUser(username: string, password: string): Promise<User | null>;

  // Student operations
  getStudent(id: string): Promise<Student | undefined>;
  getStudentByNis(nis: string): Promise<Student | undefined>;
  getStudentByQrCode(qrCode: string): Promise<Student | undefined>;
  getStudents(filters?: { search?: string; class?: string; isActive?: boolean }): Promise<Student[]>;
  createStudent(student: InsertStudent): Promise<Student>;
  updateStudent(id: string, student: Partial<InsertStudent>): Promise<Student>;
  deleteStudent(id: string): Promise<void>;

  // Attendance operations
  getAttendanceRecord(id: string): Promise<AttendanceWithStudent | undefined>;
  getAttendanceRecords(filters?: {
    studentId?: string;
    date?: Date;
    startDate?: Date;
    endDate?: Date;
    status?: string;
    class?: string;
  }): Promise<AttendanceWithStudent[]>;
  createAttendanceRecord(attendance: InsertAttendance): Promise<AttendanceRecord>;
  updateAttendanceRecord(id: string, attendance: Partial<InsertAttendance>): Promise<AttendanceRecord>;

  // Statistics
  getAttendanceStats(date?: Date): Promise<{
    totalStudents: number;
    presentToday: number;
    lateToday: number;
    absentToday: number;
    sickToday: number;
    permissionToday: number;
  }>;

  getStudentAttendanceReport(filters?: {
    startDate?: Date;
    endDate?: Date;
    class?: string;
  }): Promise<Array<{
    student: Student;
    present: number;
    late: number;
    sick: number;
    permission: number;
    absent: number;
    percentage: number;
  }>>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const hashedPassword = await bcrypt.hash(insertUser.password, 10);
    const [user] = await db
      .insert(users)
      .values({
        ...insertUser,
        password: hashedPassword,
      })
      .returning();
    return user;
  }

  async validateUser(username: string, password: string): Promise<User | null> {
    const user = await this.getUserByUsername(username);
    if (!user) return null;

    const isValid = await bcrypt.compare(password, user.password);
    return isValid ? user : null;
  }

  // Student operations
  async getStudent(id: string): Promise<Student | undefined> {
    const [student] = await db.select().from(students).where(eq(students.id, id));
    return student;
  }

  async getStudentByNis(nis: string): Promise<Student | undefined> {
    const [student] = await db.select().from(students).where(eq(students.nis, nis));
    return student;
  }

  async getStudentByQrCode(qrCode: string): Promise<Student | undefined> {
    const [student] = await db.select().from(students).where(eq(students.qrCode, qrCode));
    return student;
  }

  async getStudents(filters?: { search?: string; class?: string; isActive?: boolean }): Promise<Student[]> {
    let query = db.select().from(students);

    const conditions = [];

    if (filters?.search) {
      conditions.push(
        or(
          ilike(students.name, `%${filters.search}%`),
          ilike(students.nis, `%${filters.search}%`)
        )
      );
    }

    if (filters?.class) {
      conditions.push(eq(students.class, filters.class));
    }

    if (filters?.isActive !== undefined) {
      conditions.push(eq(students.isActive, filters.isActive));
    }

    if (conditions.length > 0) {
      query = query.where(and(...conditions));
    }

    return await query.orderBy(students.name);
  }

  async createStudent(insertStudent: InsertStudent): Promise<Student> {
    const qrCode = `STD_${insertStudent.nis}`;
    const [student] = await db
      .insert(students)
      .values({
        ...insertStudent,
        qrCode,
      })
      .returning();
    return student;
  }

  async updateStudent(id: string, updateData: Partial<InsertStudent>): Promise<Student> {
    const [student] = await db
      .update(students)
      .set(updateData)
      .where(eq(students.id, id))
      .returning();
    return student;
  }

  async deleteStudent(id: string): Promise<void> {
    await db.delete(students).where(eq(students.id, id));
  }

  // Attendance operations
  async getAttendanceRecord(id: string): Promise<AttendanceWithStudent | undefined> {
    const [record] = await db
      .select({
        id: attendanceRecords.id,
        studentId: attendanceRecords.studentId,
        date: attendanceRecords.date,
        time: attendanceRecords.time,
        status: attendanceRecords.status,
        notes: attendanceRecords.notes,
        recordedBy: attendanceRecords.recordedBy,
        createdAt: attendanceRecords.createdAt,
        student: students,
      })
      .from(attendanceRecords)
      .innerJoin(students, eq(attendanceRecords.studentId, students.id))
      .where(eq(attendanceRecords.id, id));

    return record as AttendanceWithStudent;
  }

  async getAttendanceRecords(filters?: {
    studentId?: string;
    date?: Date;
    startDate?: Date;
    endDate?: Date;
    status?: string;
    class?: string;
  }): Promise<AttendanceWithStudent[]> {
    let query = db
      .select({
        id: attendanceRecords.id,
        studentId: attendanceRecords.studentId,
        date: attendanceRecords.date,
        time: attendanceRecords.time,
        status: attendanceRecords.status,
        notes: attendanceRecords.notes,
        recordedBy: attendanceRecords.recordedBy,
        createdAt: attendanceRecords.createdAt,
        student: students,
      })
      .from(attendanceRecords)
      .innerJoin(students, eq(attendanceRecords.studentId, students.id));

    const conditions = [];

    if (filters?.studentId) {
      conditions.push(eq(attendanceRecords.studentId, filters.studentId));
    }

    if (filters?.date) {
      const startOfDay = new Date(filters.date);
      startOfDay.setHours(0, 0, 0, 0);
      const endOfDay = new Date(filters.date);
      endOfDay.setHours(23, 59, 59, 999);

      conditions.push(
        and(
          gte(attendanceRecords.date, startOfDay),
          lte(attendanceRecords.date, endOfDay)
        )
      );
    }

    if (filters?.startDate) {
      conditions.push(gte(attendanceRecords.date, filters.startDate));
    }

    if (filters?.endDate) {
      conditions.push(lte(attendanceRecords.date, filters.endDate));
    }

    if (filters?.status) {
      conditions.push(eq(attendanceRecords.status, filters.status as any));
    }

    if (filters?.class) {
      conditions.push(eq(students.class, filters.class));
    }

    if (conditions.length > 0) {
      query = query.where(and(...conditions));
    }

    const results = await query.orderBy(desc(attendanceRecords.time));
    return results as AttendanceWithStudent[];
  }

  async createAttendanceRecord(insertAttendance: InsertAttendance): Promise<AttendanceRecord> {
    const [record] = await db
      .insert(attendanceRecords)
      .values(insertAttendance)
      .returning();
    return record;
  }

  async updateAttendanceRecord(id: string, updateData: Partial<InsertAttendance>): Promise<AttendanceRecord> {
    const [record] = await db
      .update(attendanceRecords)
      .set(updateData)
      .where(eq(attendanceRecords.id, id))
      .returning();
    return record;
  }

  // Statistics
  async getAttendanceStats(date: Date = new Date()): Promise<{
    totalStudents: number;
    presentToday: number;
    lateToday: number;
    absentToday: number;
    sickToday: number;
    permissionToday: number;
  }> {
    const startOfDay = new Date(date);
    startOfDay.setHours(0, 0, 0, 0);
    const endOfDay = new Date(date);
    endOfDay.setHours(23, 59, 59, 999);

    const [totalStudentsResult] = await db
      .select({ count: sql<number>`count(*)`.as('count') })
      .from(students)
      .where(eq(students.isActive, true));

    const todayAttendance = await db
      .select({
        status: attendanceRecords.status,
        count: sql<number>`count(*)`.as('count'),
      })
      .from(attendanceRecords)
      .where(
        and(
          gte(attendanceRecords.date, startOfDay),
          lte(attendanceRecords.date, endOfDay)
        )
      )
      .groupBy(attendanceRecords.status);

    const stats = {
      totalStudents: totalStudentsResult.count,
      presentToday: 0,
      lateToday: 0,
      absentToday: 0,
      sickToday: 0,
      permissionToday: 0,
    };

    todayAttendance.forEach((item) => {
      switch (item.status) {
        case 'present':
          stats.presentToday = item.count;
          break;
        case 'late':
          stats.lateToday = item.count;
          break;
        case 'absent':
          stats.absentToday = item.count;
          break;
        case 'sick':
          stats.sickToday = item.count;
          break;
        case 'permission':
          stats.permissionToday = item.count;
          break;
      }
    });

    return stats;
  }

  async getStudentAttendanceReport(filters?: {
    startDate?: Date;
    endDate?: Date;
    class?: string;
  }): Promise<Array<{
    student: Student;
    present: number;
    late: number;
    sick: number;
    permission: number;
    absent: number;
    percentage: number;
  }>> {
    const conditions = [eq(students.isActive, true)];

    if (filters?.class) {
      conditions.push(eq(students.class, filters.class));
    }

    let studentsQuery = db.select().from(students);
    if (conditions.length > 0) {
      studentsQuery = studentsQuery.where(and(...conditions));
    }

    const allStudents = await studentsQuery;
    const report = [];

    for (const student of allStudents) {
      const conditions = [eq(attendanceRecords.studentId, student.id)];

      if (filters?.startDate) {
        conditions.push(gte(attendanceRecords.date, filters.startDate));
      }

      if (filters?.endDate) {
        conditions.push(lte(attendanceRecords.date, filters.endDate));
      }

      const attendanceData = await db
        .select({
          status: attendanceRecords.status,
          count: sql<number>`count(*)`.as('count'),
        })
        .from(attendanceRecords)
        .where(and(...conditions))
        .groupBy(attendanceRecords.status);

      const stats = {
        present: 0,
        late: 0,
        sick: 0,
        permission: 0,
        absent: 0,
      };

      attendanceData.forEach((item) => {
        switch (item.status) {
          case 'present':
            stats.present = item.count;
            break;
          case 'late':
            stats.late = item.count;
            break;
          case 'sick':
            stats.sick = item.count;
            break;
          case 'permission':
            stats.permission = item.count;
            break;
          case 'absent':
            stats.absent = item.count;
            break;
        }
      });

      const totalDays = stats.present + stats.late + stats.sick + stats.permission + stats.absent;
      const attendedDays = stats.present + stats.late;
      const percentage = totalDays > 0 ? (attendedDays / totalDays) * 100 : 0;

      report.push({
        student,
        ...stats,
        percentage: Math.round(percentage * 10) / 10,
      });
    }

    return report;
  }
}

export const storage = new DatabaseStorage();

